import { React, useEffect }from 'react'
import { DrumButton, Toggle } from './components'


const App = () => {
  let volume = .5;
  useEffect(() => {
    let slider = document.getElementById("myRange");
    let output = document.getElementById("demo");
    output.innerHTML = slider.value;
    slider.oninput = function() {
      output.innerHTML = this.value * 100;
      volume = this.value;
      slider.value = this.value;
    }
  }) 
  return (
    <div className='app__wrapper'>
        <div className='drumContainer'>
          <div className='buttonContainer'>
            <DrumButton 
              text={"Q"}
              volume={volume}
              audio={new Audio('https://s3.amazonaws.com/freecodecamp/drums/Heater-1.mp3')}
            />
            <DrumButton text={"W"}/>
            <DrumButton text={"E"}/>
            <DrumButton text={"A"}/>
            <DrumButton text={"S"}/>
            <DrumButton text={"D"}/>
            <DrumButton text={"Z"}/>
            <DrumButton text={"X"}/>
            <DrumButton text={"C"}/>
          </div>
          <div className='contentContainer'>
            <div className='toggleText'>Power</div>
            <Toggle power={true}/>
            <div className='soundText' id='demo'>test</div>
            <input type="range" min="0" max="1" value=".5" class="slider" id="myRange"></input>
            <div className='toggleText'>Bank</div>
            <Toggle power={false}/>
          </div>
        </div>
    </div>
  )
}

export default App
