import { React, useState }from 'react'

const Toggle = ({power, setState, getState}) => {
  const [isChecked, setIsChecked] = useState(power)
  
  const checkHandler = () => {
    setIsChecked(!isChecked)
    setState(!getState)
  }
  return (
    <div className='toggle'>
        <label class="switch">
          <input type="checkbox" id='checkbox' onChange={checkHandler} checked={isChecked}/>
          <span class="sliderToggle"/>
        </label>
    </div>
  )
}

export default Toggle
