import { React, useEffect }from 'react'

const Toggle = ({power}) => {
  useEffect(() => {
    if({power}){
      document.getElementById("checkbox").checked = true;
    }
  })
  return (
    <div className='toggle'>
        <label class="switch">
          <input type="checkbox" id='checkbox'/>
          <span class="sliderToggle"/>
        </label>
    </div>
  )
}

export default Toggle
