import React from 'react'

const drumButton = ({text, audio, altAudio, getVolume, getPower, getBank}) => {
  const handleClick = () => {
    console.log("press")
    console.log(getVolume)
    console.log(power)
    console.log(bank)
    audio.volume = getVolume;
    audio.play();
  }
  return (
    <div className='drumButton' onClick={handleClick}>
        {text}
    </div>
  )
}

export default drumButton
